// src/components/MusicPlayer.js
import React, { useState, useRef, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Volume2, VolumeX } from 'lucide-react';

const MusicPlayer = () => {
  const [isMuted, setIsMuted] = useState(true); // Start muted
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);
  const hasUnmutedRef = useRef(false);

  // Initialize audio and auto-unmute
  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    // Set initial volume and start muted
    audio.volume = 0.3;
    audio.muted = true;

    const playAndUnmute = async () => {
      if (hasUnmutedRef.current) return;

      try {
        // First ensure audio is playing
        if (audio.paused) {
          await audio.play();
          setIsPlaying(true);
        }

        // Then unmute
        audio.muted = false;
        setIsMuted(false);
        hasUnmutedRef.current = true;
        console.log('Audio playing and unmuted');

      } catch (err) {
        console.log('Play/unmute failed:', err);
      }
    };

    const handleScroll = () => {
      playAndUnmute();
      document.removeEventListener('scroll', handleScroll);
    };

    // Function to handle user interaction
    const handleUserInteraction = async () => {
      await playAndUnmute();
    };

    // Try to play immediately (muted)
    const tryPlay = async () => {
      try {
        await audio.play();
        setIsPlaying(true);
        console.log('Autoplay successful (muted)');

        // Unmute after 0.5 seconds
        const timeoutId = setTimeout(() => {
          playAndUnmute();
        }, 500);

        // Also unmute on first scroll
        document.addEventListener('scroll', handleScroll, { passive: true });

        return () => {
          clearTimeout(timeoutId);
          document.removeEventListener('scroll', handleScroll);
        };

      } catch (err) {
        console.log('Autoplay blocked, waiting for user interaction:', err);

        // If autoplay fails, set up multiple interaction handlers
        const interactionHandler = () => {
          playAndUnmute();
          // Remove all listeners after first successful interaction
          document.removeEventListener('click', interactionHandler);
          document.removeEventListener('touchstart', interactionHandler);
          document.removeEventListener('scroll', interactionHandler);
        };

        document.addEventListener('click', interactionHandler);
        document.addEventListener('touchstart', interactionHandler);
        document.addEventListener('scroll', interactionHandler, { passive: true });

        return () => {
          document.removeEventListener('click', interactionHandler);
          document.removeEventListener('touchstart', interactionHandler);
          document.removeEventListener('scroll', interactionHandler);
        };
      }
    };

    tryPlay();
  }, []);

  const toggleMute = () => {
    if (audioRef.current) {
      // If audio is paused and user is unmuting, try to play first
      if (audioRef.current.paused && isMuted) {
        audioRef.current.play()
          .then(() => {
            setIsPlaying(true);
            audioRef.current.muted = false;
            setIsMuted(false);
          })
          .catch(err => console.log('Play failed on unmute:', err));
      } else {
        // Just toggle mute state
        audioRef.current.muted = !isMuted;
        setIsMuted(!isMuted);
      }
    }
  };

  // Debug: Log audio state
  useEffect(() => {
    const interval = setInterval(() => {
      if (audioRef.current) {
        console.log('Audio State:', {
          paused: audioRef.current.paused,
          muted: audioRef.current.muted,
          volume: audioRef.current.volume,
          readyState: audioRef.current.readyState,
          isPlaying,
          isMuted
        });
      }
    }, 5000);

    return () => clearInterval(interval);
  }, [isPlaying, isMuted]);

  return (
    <>
      {/* Hidden audio element */}
      <audio
        ref={audioRef}
        loop
        preload="auto"
        style={{ display: 'none' }}
      >
        {/* Make sure this file exists in public/music/ */}
        <source src="/music/wedding-music.mp3" type="audio/mpeg" />
        <source src="/music/wedding-music.ogg" type="audio/ogg" />
        Your browser does not support the audio element.
      </audio>

      {/* Mute/Unmute Button */}
      <motion.button
        initial={{ opacity: 0, scale: 0 }}
        animate={{ opacity: 1, scale: 1 }}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={toggleMute}
        className="fixed bottom-4 right-4 z-50 w-12 h-12 bg-gradient-to-br from-burgundy to-dark-burgundy rounded-2xl flex items-center justify-center shadow-2xl border-2 border-gold/30 backdrop-blur-sm"
        title={isMuted ? "Unmute music" : "Mute music"}
      >
        {isMuted ? (
          <VolumeX className="w-6 h-6 text-gold" />
        ) : (
          <Volume2 className="w-6 h-6 text-gold" />
        )}
      </motion.button>
    </>
  );
};

export default MusicPlayer;
