import { motion, AnimatePresence, useScroll, useTransform, useSpring } from 'framer-motion';
import { 
  Calendar, 
  Clock, 
  MapPin, 
  Users, 
  Heart,
  Sparkles,
  ChevronDown,
  ChevronUp,
  Flower2,
  Gem,
  Crown
} from 'lucide-react';

const Information = () => {
  const [activeTab, setActiveTab] = useState('ceremony');
  const [expandedCard, setExpandedCard] = useState(null);
  const sectionRef = useRef(null);

  // Scroll-based animations
  const { scrollYProgress } = useScroll({
    target: sectionRef,
    offset: ["start end", "end start"]
  });

  // Parallax effects for floating elements
  const y1 = useTransform(scrollYProgress, [0, 1], [0, -100]);
  const y2 = useTransform(scrollYProgress, [0, 1], [0, 100]);
  const x1 = useTransform(scrollYProgress, [0, 1], [-100, 100]);
  const x2 = useTransform(scrollYProgress, [0, 1], [100, -100]);
  const rotate = useTransform(scrollYProgress, [0, 1], [0, 360]);

  // Spring physics for smoother animations
  const springY1 = useSpring(y1, { stiffness: 100, damping: 30 });
  const springY2 = useSpring(y2, { stiffness: 100, damping: 30 });
  const springX1 = useSpring(x1, { stiffness: 100, damping: 30 });
  const springX2 = useSpring(x2, { stiffness: 100, damping: 30 });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.15
      }
    }
  };

  const itemVariants = {
    hidden: { 
      opacity: 0, 
      y: 40,
      scale: 0.95
    },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        duration: 0.6,
        ease: [0.25, 0.1, 0.25, 1]
      }
    }
  };

  const cardVariants = {
    hidden: { 
      opacity: 0,
      y: 30,
      rotateX: -10
    },
    visible: {
      opacity: 1,
      y: 0,
      rotateX: 0,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    },
    hover: {
      y: -8,
      scale: 1.02,
      transition: {
        duration: 0.3,
        ease: "easeInOut"
      }
    }
  };

  const scrollCardVariants = {
    offscreen: {
      x: -100,
      opacity: 0,
      rotate: -5
    },
    onscreen: {
      x: 0,
      opacity: 1,
      rotate: 0,
      transition: {
        type: "spring",
        bounce: 0.4,
        duration: 1
      }
    }
  };

  const scrollCardVariantsRight = {
    offscreen: {
      x: 100,
      opacity: 0,
      rotate: 5
    },
    onscreen: {
      x: 0,
      opacity: 1,
      rotate: 0,
      transition: {
        type: "spring",
        bounce: 0.4,
        duration: 1
      }
    }
  };

  const eventDetails = {
    ceremony: {
      icon: 'ðŸ’’',
      title: 'Wedding Ceremony',
      color: 'from-blue-500/20 to-purple-500/20',
      borderColor: 'border-blue-200/50',
      details: [
        {
          icon: Calendar,
          title: 'December 15, 2024',
          subtitle: 'Saturday Evening',
          description: 'Join us as we exchange our sacred vows in a traditional ceremony filled with love and blessings.'
        },
        {
          icon: Clock,
          title: '4:00 PM - 5:30 PM',
          subtitle: 'Sacred Vows',
          description: 'The ceremony will begin promptly at 4:00 PM. Please arrive 30 minutes early for seating.'
        },
        {
          icon: MapPin,
          title: 'Grand Palace Hotel',
          subtitle: 'Marine Drive, Mumbai',
          description: 'A beautiful waterfront venue with stunning views of the Arabian Sea. Valet parking available.'
        },
        {
          icon: Users,
          title: 'Guest Attire',
          subtitle: 'Formal Traditional Wear',
          description: 'Traditional Indian attire or formal western wear. Embrace the elegance of our special day.'
        }
      ]
    },
    reception: {
      icon: 'ðŸŽ‰',
      title: 'Reception Celebration',
      color: 'from-pink-500/20 to-orange-500/20',
      borderColor: 'border-pink-200/50',
      details: [
        {
          icon: Calendar,
          title: 'December 15, 2024',
          subtitle: 'Saturday Night',
          description: 'An evening of celebration, fine dining, and dancing under the stars.'
        },
        {
          icon: Clock,
          title: '6:00 PM onwards',
          subtitle: 'Dinner & Festivities',
          description: 'Cocktail hour begins at 6:00 PM, followed by dinner and dancing throughout the night.'
        },
        {
          icon: MapPin,
          title: 'Grand Ballroom',
          subtitle: 'Same Venue',
          description: 'The celebration continues in our beautifully decorated grand ballroom with live music.'
        },
        {
          icon: Users,
          title: 'Evening Attire',
          subtitle: 'Cocktail & Celebration',
          description: 'Elegant cocktail attire. Come ready to dance and celebrate with us!'
        }
      ]
    }
  };

  const toggleCard = (index) => {
    setExpandedCard(expandedCard === index ? null : index);
  };

  return (
    <section 
      id="information" 
      ref={sectionRef}
      className="relative bg-gradient-to-b from-white to-cream/30 overflow-hidden"
    >
      {/* Scroll-based Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Left-to-right moving elements */}
        <motion.div
          style={{ x: springX1, y: springY1 }}
          className="absolute top-1/4 left-1/4 w-72 h-72 bg-gold/5 rounded-full blur-3xl"
        />

        {/* Right-to-left moving elements */}
        <motion.div
          style={{ x: springX2, y: springY2 }}
          className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-burgundy/5 rounded-full blur-3xl"
        />

        {/* Rotating decorative elements */}
        <motion.div
          style={{ rotate }}
          className="absolute top-1/3 left-1/3"
        >
          <Flower2 className="w-12 h-12 text-gold/30" />
        </motion.div>

        <motion.div
          style={{ rotate: useTransform(scrollYProgress, [0, 1], [0, -360]) }}
          className="absolute bottom-1/3 right-1/3"
        >
          <Gem className="w-10 h-10 text-burgundy/30" />
        </motion.div>
      </div>

      {/* Scroll-triggered Floating Elements */}
      <div className="absolute inset-0">
        {[...Array(8)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute"
            style={{
              left: `${10 + i * 10}%`,
              top: `${20 + i * 8}%`,
            }}
            initial={{ x: i % 2 === 0 ? -100 : 100, opacity: 0 }}
            whileInView={{ 
              x: 0, 
              opacity: 0.6,
              transition: {
                duration: 1.5,
                delay: i * 0.2,
                ease: "easeOut"
              }
            }}
            viewport={{ once: true }}
          >
            <Crown className="w-6 h-6 text-gold/40" />
          </motion.div>
        ))}
      </div>

      <div className="section-padding relative z-10">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, margin: "-50px" }}
          className="w-full max-w-6xl mx-auto"
        >
          {/* Header Section with Scroll Animation */}
          <motion.div 
            variants={itemVariants} 
            className="text-center mb-16"
          >
            <motion.div
              initial={{ scale: 0 }}
              whileInView={{ scale: 1 }}
              transition={{ 
                type: "spring", 
                stiffness: 200, 
                damping: 15,
                delay: 0.2 
              }}
              className="inline-flex items-center gap-3 mb-6"
            >
              <Sparkles className="w-8 h-8 text-gold" />
              <h2 className="luxury-heading bg-gradient-to-r from-burgundy to-gold bg-clip-text text-transparent">
                Our Wedding Day
              </h2>
              <Sparkles className="w-8 h-8 text-gold" />
            </motion.div>

            <motion.div
              initial={{ width: 0 }}
              whileInView={{ width: 96 }}
              transition={{ duration: 1, delay: 0.5 }}
              className="w-24 h-1 bg-gradient-to-r from-gold to-light-gold mx-auto rounded-full mb-6"
            />

            <motion.p 
              variants={itemVariants}
              className="luxury-text text-xl text-dark-burgundy/80 max-w-2xl mx-auto leading-relaxed"
            >
              Join us for an unforgettable celebration of love, commitment, and new beginnings
            </motion.p>
          </motion.div>

          {/* Interactive Tabs with Scroll Animation */}
          <motion.div 
            variants={itemVariants} 
            className="flex justify-center mb-12"
          >
            <motion.div
              initial={{ y: 50, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.8, delay: 0.3 }}
              className="bg-white/80 backdrop-blur-sm rounded-2xl p-2 border border-gold/20 shadow-lg"
            >
              {Object.entries(eventDetails).map(([key, event]) => (
                <motion.button
                  key={key}
                  onClick={() => setActiveTab(key)}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className={`px-8 py-4 rounded-xl font-playfair font-semibold text-lg transition-all duration-300 ${
                    activeTab === key
                      ? 'bg-gradient-to-r from-gold to-light-gold text-burgundy shadow-lg'
                      : 'text-dark-burgundy/70 hover:text-burgundy hover:bg-white/50'
                  }`}
                >
                  <span className="flex items-center gap-3">
                    <span className="text-2xl">{event.icon}</span>
                    {event.title}
                  </span>
                </motion.button>
              ))}
            </motion.div>
          </motion.div>

          {/* Event Details with Scroll-triggered Animations */}
          <AnimatePresence mode="wait">
            <motion.div
              key={activeTab}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
              className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-12"
            >
              {eventDetails[activeTab].details.map((detail, index) => (
                <motion.div
                  key={index}
                  variants={index % 2 === 0 ? scrollCardVariants : scrollCardVariantsRight}
                  initial="offscreen"
                  whileInView="onscreen"
                  viewport={{ once: true, amount: 0.3 }}
                  whileHover="hover"
                  className={`bg-white/90 backdrop-blur-sm rounded-2xl p-6 border-2 ${eventDetails[activeTab].borderColor} shadow-xl cursor-pointer overflow-hidden`}
                  onClick={() => toggleCard(index)}
                >
                  <div className="flex items-start gap-4">
                    <motion.div 
                      whileHover={{ scale: 1.1, rotate: 5 }}
                      className="flex-shrink-0 w-14 h-14 bg-gradient-to-br from-gold to-light-gold rounded-2xl flex items-center justify-center shadow-lg"
                    >
                      <detail.icon className="w-6 h-6 text-burgundy" />
                    </motion.div>

                    <div className="flex-1 min-w-0">
                      <h3 className="luxury-subheading text-lg mb-1 text-burgundy">
                        {detail.title}
                      </h3>
                      <p className="luxury-text text-gold font-semibold text-sm mb-2">
                        {detail.subtitle}
                      </p>

                      <AnimatePresence>
                        {expandedCard === index && (
                          <motion.p
                            initial={{ opacity: 0, height: 0 }}
                            animate={{ opacity: 1, height: 'auto' }}
                            exit={{ opacity: 0, height: 0 }}
                            className="luxury-text text-dark-burgundy/80 text-sm leading-relaxed"
                          >
                            {detail.description}
                          </motion.p>
                        )}
                      </AnimatePresence>
                    </div>

                    <motion.div
                      animate={{ rotate: expandedCard === index ? 180 : 0 }}
                      className="flex-shrink-0 w-8 h-8 bg-gold/10 rounded-full flex items-center justify-center"
                    >
                      {expandedCard === index ? (
                        <ChevronUp className="w-4 h-4 text-gold" />
                      ) : (
                        <ChevronDown className="w-4 h-4 text-gold" />
                      )}
                    </motion.div>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          </AnimatePresence>

          {/* Special Note Card with Scroll Effects */}
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            whileInView={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            whileHover={{ 
              scale: 1.02,
              transition: { duration: 0.3 }
            }}
            className="relative overflow-hidden rounded-3xl bg-gradient-to-br from-white to-gold/10 border-2 border-gold/30 shadow-2xl mb-12"
          >
            {/* Animated Background */}
            <motion.div
              animate={{
                backgroundPosition: ['0% 0%', '100% 100%'],
              }}
              transition={{
                duration: 10,
                repeat: Infinity,
                ease: "linear"
              }}
              className="absolute inset-0 bg-gradient-to-br from-transparent via-gold/5 to-transparent bg-[length:200%_200%]"
            />

            <div className="relative z-10 p-8 text-center">
              <motion.div
                animate={{
                  rotate: [0, 5, 0, -5, 0],
                }}
                transition={{
                  duration: 4,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
                className="inline-flex items-center gap-3 mb-6"
              >
                <Heart className="w-8 h-8 text-burgundy fill-current" />
                <h3 className="luxury-subheading text-2xl text-burgundy">
                  A Heartfelt Note
                </h3>
                <Heart className="w-8 h-8 text-burgundy fill-current" />
              </motion.div>

              <motion.p
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.2 }}
                className="luxury-text text-lg leading-relaxed text-dark-burgundy/90 max-w-3xl mx-auto mb-6"
              >
                Your presence is the greatest gift we could ask for as we begin our forever together. 
                Come ready to celebrate with love, laughter, and joy that will create memories to last a lifetime.
              </motion.p>

              <motion.div
                initial={{ scale: 0 }}
                whileInView={{ scale: 1 }}
                transition={{ 
                  type: "spring", 
                  stiffness: 200, 
                  damping: 15,
                  delay: 0.5 
                }}
                className="w-32 h-1 bg-gradient-to-r from-gold to-light-gold mx-auto rounded-full"
              />
            </div>
          </motion.div>

          {/* Scroll-triggered Countdown Section */}
          <motion.div
            initial={{ y: 50, opacity: 0 }}
            whileInView={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            className="text-center"
          >
            <motion.div
              whileHover={{ scale: 1.05 }}
              className="inline-flex items-center gap-4 px-8 py-4 bg-white/80 backdrop-blur-sm rounded-2xl border border-gold/20 shadow-lg cursor-pointer"
            >
              <Calendar className="w-7 h-7 text-gold" />
              <span className="luxury-text text-burgundy font-semibold text-lg">
                Save the Date: December 15, 2024
              </span>
            </motion.div>
          </motion.div>
        </motion.div>
      </div>

      {/* Continuous Flow Elements - Connect to next component */}
      <motion.div
        initial={{ scaleY: 0 }}
        whileInView={{ scaleY: 1 }}
        transition={{ duration: 1.5, ease: "easeOut" }}
        className="absolute bottom-0 left-1/2 transform -translate-x-1/2 w-1 h-32 bg-gradient-to-t from-gold to-transparent"
      />
    </section>
  );
};

export default Information;
