// src/components/MusicPlayer.js (Aggressive Approach)
import React, { useState, useRef, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Volume2, VolumeX } from 'lucide-react';

const MusicPlayer = () => {
  const [isMuted, setIsMuted] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    audio.volume = 0.3;

    // Multiple attempts to play
    const playWithRetry = async (retries = 3) => {
      for (let i = 0; i < retries; i++) {
        try {
          await audio.play();
          console.log('Music playback started successfully');
          return;
        } catch (err) {
          console.log(`Play attempt ${i + 1} failed:`, err);
          if (i < retries - 1) {
            await new Promise(resolve => setTimeout(resolve, 1000));
          }
        }
      }
      console.log('All play attempts failed, waiting for user interaction');
    };

    // Initial play attempt
    playWithRetry();

    // Set up global click handler to start playback
    const handleGlobalClick = async () => {
      if (audio.paused) {
        try {
          await audio.play();
          console.log('Playback started via global click');
        } catch (err) {
          console.log('Global click playback failed:', err);
        }
      }
    };

    document.addEventListener('click', handleGlobalClick);

    return () => {
      document.removeEventListener('click', handleGlobalClick);
    };
  }, []);

  const toggleMute = () => {
    if (audioRef.current) {
      audioRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  return (
    <>
      <audio
        ref={audioRef}
        loop
        preload="auto"
        style={{ display: 'none' }}
      >
        {/* IMPORTANT: Make sure your music file exists at this path */}
        <source src="/music/wedding-music.mp3" type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>

      <motion.button
        initial={{ opacity: 0, scale: 0 }}
        animate={{ opacity: 1, scale: 1 }}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={toggleMute}
        className="fixed bottom-4 right-4 z-50 w-12 h-12 bg-gradient-to-br from-burgundy to-dark-burgundy rounded-2xl flex items-center justify-center shadow-2xl border-2 border-gold/30 backdrop-blur-sm"
      >
        {isMuted ? (
          <VolumeX className="w-6 h-6 text-gold" />
        ) : (
          <Volume2 className="w-6 h-6 text-gold" />
        )}
      </motion.button>
    </>
  );
};

export default MusicPlayer;
