// src/components/Footer.js
import React from 'react';
import { motion } from 'framer-motion';
import { Heart, Mail, Instagram, Linkedin } from 'lucide-react';

const Footer = () => {
  const currentYear = new Date().getFullYear();

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    }
  };

  const socialLinks = [
    {
      icon: Instagram,
      href: "https://instagram.com/spotcard.in",
      label: "Instagram"
    },
    {
      icon: Linkedin,
      href: "https://www.linkedin.com/company/spotcard-business-solution",
      label: "LinkedIn"
    },
    {
      icon: Mail,
      href: "mailto:hello@spotcard.in",
      label: "Email"
    }
  ];

  return (
    <footer className="bg-gradient-to-br from-dark-burgundy to-burgundy border-t border-gold/20 relative">
      {/* Top decorative border */}
      <motion.div
        initial={{ scaleX: 0 }}
        whileInView={{ scaleX: 1 }}
        transition={{ duration: 1, ease: "easeOut" }}
        className="h-0.5 bg-gradient-to-r from-transparent via-gold to-transparent"
      />

      <motion.div
        variants={containerVariants}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
        className="max-w-6xl mx-auto px-6 py-8"
      >
        {/* Logo and Social Links */}
        <div className="flex flex-col items-center gap-6 mb-6">
          {/* Logo */}
          <motion.div
            variants={itemVariants}
            className="flex items-center gap-3"
          >
            <img 
              src="/logo2.png" 
              alt="SpotCard" 
              className="w-22 h-22 object-contain"
            />
            {/* <div>
              <h3 className="luxury-heading text-xl text-gold">SpotCard</h3>
              <p className="luxury-text text-white/60 text-xs">Business Solutions</p>
            </div> */}
          </motion.div>

          {/* Social Links */}
          <motion.div
            variants={itemVariants}
            className="flex items-center gap-4"
          >
            {socialLinks.map((social, index) => (
              <motion.a
                key={index}
                href={social.href}
                target="_blank"
                rel="noopener noreferrer"
                variants={itemVariants}
                whileHover={{ scale: 1.2, y: -2 }}
                whileTap={{ scale: 0.95 }}
                className="w-10 h-10 bg-gold/10 rounded-xl flex items-center justify-center hover:bg-gold/20 transition-colors"
              >
                <social.icon className="w-5 h-5 text-gold" />
              </motion.a>
            ))}
          </motion.div>
        </div>

        {/* Copyright */}
        <motion.div
          variants={itemVariants}
          className="text-center border-t border-gold/10 pt-4"
        >
          <div className="flex items-center justify-center gap-2 mb-2">
            <p className="luxury-text text-white/60 text-sm">
              Â© {currentYear} SpotCard
            </p>
            <motion.div
              animate={{ scale: [1, 1.2, 1] }}
              transition={{ duration: 2, repeat: Infinity }}
              className="text-gold"
            >
              <Heart className="w-3 h-3 fill-current" />
            </motion.div>
          </div>

          <p className="luxury-text text-white/40 text-xs">
            Transforming business interactions
          </p>
        </motion.div>
      </motion.div>
    </footer>
  );
};

export default Footer;
