// src/components/MusicPlayer.js
import React, { useState, useRef, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Volume2, VolumeX } from 'lucide-react';

const MusicPlayer = () => {
  const [isMuted, setIsMuted] = useState(true); // Start muted
  const audioRef = useRef(null);

  // Initialize audio and auto-unmute
  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    // Set initial volume and start muted
    audio.volume = 0.3;
    audio.muted = true;

    // Function to handle user interaction
    const handleUserInteraction = async () => {
      if (audio.paused) {
        try {
          await audio.play();
          console.log('Playback started after user interaction');
          // Unmute after play
          audio.muted = false;
          setIsMuted(false);
        } catch (err) {
          console.log('Playback failed:', err);
        }
      }
    };

    // Try to play immediately (muted)
    const tryPlay = async () => {
      try {
        await audio.play();
        console.log('Autoplay successful (muted)');

        // Automatically unmute after 2 seconds
        setTimeout(() => {
          if (!audio.muted) return; // Skip if already unmuted
          audio.muted = false;
          setIsMuted(false);
          console.log('Auto-unmuted successfully');
        }, 2000);

      } catch (err) {
        console.log('Autoplay blocked, waiting for user interaction:', err);

        // If autoplay fails, set up click handler to start playback
        document.addEventListener('click', handleUserInteraction, { once: true });
        document.addEventListener('touchstart', handleUserInteraction, { once: true });
      }
    };

    tryPlay();

    // Cleanup function
    return () => {
      document.removeEventListener('click', handleUserInteraction);
      document.removeEventListener('touchstart', handleUserInteraction);
    };
  }, []);

  const toggleMute = () => {
    if (audioRef.current) {
      audioRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  return (
    <>
      {/* Hidden audio element */}
      <audio
        ref={audioRef}
        loop
        preload="auto"
        style={{ display: 'none' }}
      >
        {/* Add your music file - make sure the path is correct */}
        <source src="/music/wedding-music.mp3" type="audio/mpeg" />
        <source src="/music/wedding-music.ogg" type="audio/ogg" />
        <source src="/music/wedding-music.wav" type="audio/wav" />
        Your browser does not support the audio element.
      </audio>

      {/* Mute/Unmute Button */}
      <motion.button
        initial={{ opacity: 0, scale: 0 }}
        animate={{ opacity: 1, scale: 1 }}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={toggleMute}
        className="fixed bottom-4 right-4 z-50 w-12 h-12 bg-gradient-to-br from-burgundy to-dark-burgundy rounded-2xl flex items-center justify-center shadow-2xl border-2 border-gold/30 backdrop-blur-sm"
        title={isMuted ? "Unmute music" : "Mute music"}
      >
        {isMuted ? (
          <VolumeX className="w-6 h-6 text-gold" />
        ) : (
          <Volume2 className="w-6 h-6 text-gold" />
        )}
      </motion.button>
    </>
  );
};

export default MusicPlayer;
