// src/components/Hero.js
import React from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  const textVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { duration: 1, ease: "easeOut" }
    }
  };

  const imageVariants = {
    hidden: { scale: 0.8, opacity: 0 },
    visible: { 
      scale: 1, 
      opacity: 1,
      transition: { duration: 1.2, ease: "easeOut" }
    }
  };

  return (
    <section className="section-padding bg-gradient-to-br from-cream to-light-gold relative overflow-hidden">
      {/* Animated background elements */}
      <motion.div
        animate={{ y: [0, -20, 0], rotate: [0, 5, 0] }}
        transition={{ duration: 6, repeat: Infinity, ease: "easeInOut" }}
        className="absolute top-10 left-10 w-24 h-24 border-2 border-gold rounded-full opacity-30"
      />

      <motion.div
        initial="hidden"
        animate="visible"
        className="relative z-10 text-center"
      >
        <motion.h1 
          variants={textVariants}
          className="luxury-heading mb-6"
        >
          Sarah & Michael
        </motion.h1>

        <motion.p 
          variants={textVariants}
          className="luxury-text text-2xl mb-8"
        >
          Invite you to celebrate their wedding
        </motion.p>

        <motion.div
          variants={imageVariants}
          className="w-96 h-[500px] mx-auto bg-cover bg-center border-8 border-gold rounded-lg shadow-2xl"
          style={{ backgroundImage: 'url(/api/placeholder/400/500)' }}
        />

        <motion.p 
          variants={textVariants}
          className="luxury-text text-xl mt-8"
        >
          15th December 2024
        </motion.p>
      </motion.div>
    </section>
  );
};

export default Hero;
