// src/components/Information.js
import React from 'react';
import { motion } from 'framer-motion';

const Information = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3
      }
    }
  };

  const itemVariants = {
    hidden: { y: 50, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    }
  };

  return (
    <section className="section-padding bg-cream">
      <motion.div
        variants={containerVariants}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
        className="max-w-4xl mx-auto"
      >
        <motion.h2 variants={itemVariants} className="luxury-heading mb-12 text-center">
          Wedding Celebration
        </motion.h2>

        <motion.div variants={itemVariants} className="gold-accent bg-white p-10 rounded-2xl mb-8">
          <h3 className="luxury-heading text-3xl mb-6 text-center">When & Where</h3>
          <div className="space-y-4 text-center">
            <p className="luxury-text text-xl">
              üìÖ <strong>Date:</strong> December 15, 2024
            </p>
            <p className="luxury-text text-xl">
              ‚è∞ <strong>Time:</strong> 4:00 PM onwards
            </p>
            <p className="luxury-text text-xl">
              üìç <strong>Venue:</strong> The Grand Palace Hotel, Mumbai
            </p>
          </div>
        </motion.div>

        <motion.div variants={itemVariants} className="gold-accent bg-white p-10 rounded-2xl">
          <h3 className="luxury-heading text-3xl mb-6 text-center">Dress Code</h3>
          <p className="luxury-text text-xl text-center">
            üëî <strong>Formal Attire</strong><br/>
            Traditional or Western Formal Wear
          </p>
        </motion.div>
      </motion.div>
    </section>
  );
};

export default Information;
