// src/components/MusicPlayer.js
import React, { useState, useRef, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Volume2, VolumeX } from 'lucide-react';

const MusicPlayer = () => {
  const [isMuted, setIsMuted] = useState(false);
  const [hasInteracted, setHasInteracted] = useState(false);
  const audioRef = useRef(null);

  // Initialize audio and set up interaction listeners
  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    // Set initial volume
    audio.volume = 0.3;

    // Try to play immediately (may fail due to autoplay policies)
    const tryPlay = async () => {
      try {
        await audio.play();
        console.log('Autoplay successful');
      } catch (err) {
        console.log('Autoplay blocked, waiting for user interaction');
        // Autoplay was prevented, wait for user interaction
      }
    };

    tryPlay();

    // Set up interaction handler to start playback
    const handleUserInteraction = async () => {
      if (audio.paused && !hasInteracted) {
        try {
          await audio.play();
          setHasInteracted(true);
          console.log('Playback started after user interaction');
        } catch (err) {
          console.log('Playback failed:', err);
        }
      }
    };

    // Add event listeners for user interaction
    document.addEventListener('click', handleUserInteraction);
    document.addEventListener('touchstart', handleUserInteraction);
    document.addEventListener('keydown', handleUserInteraction);

    return () => {
      document.removeEventListener('click', handleUserInteraction);
      document.removeEventListener('touchstart', handleUserInteraction);
      document.removeEventListener('keydown', handleUserInteraction);
    };
  }, [hasInteracted]);

  const toggleMute = () => {
    if (audioRef.current) {
      // If audio is paused and user clicks unmute, try to play
      if (audioRef.current.paused && isMuted) {
        audioRef.current.play().catch(console.error);
      }

      audioRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  // Force play when component mounts (alternative approach)
  useEffect(() => {
    const forcePlay = setTimeout(async () => {
      if (audioRef.current && audioRef.current.paused) {
        try {
          await audioRef.current.play();
          console.log('Forced playback successful');
        } catch (err) {
          console.log('Forced playback failed:', err);
        }
      }
    }, 1000);

    return () => clearTimeout(forcePlay);
  }, []);

  return (
    <>
      {/* Hidden audio element */}
      <audio
        ref={audioRef}
        loop
        preload="auto"
        style={{ display: 'none' }}
      >
        {/* Add your music file - make sure the path is correct */}
        <source src="/music/wedding-music.mp3" type="audio/mpeg" />
        <source src="/music/wedding-music.ogg" type="audio/ogg" />
        <source src="/music/wedding-music.wav" type="audio/wav" />
        Your browser does not support the audio element.
      </audio>

      {/* Mute/Unmute Button */}
      <motion.button
        initial={{ opacity: 0, scale: 0 }}
        animate={{ opacity: 1, scale: 1 }}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={toggleMute}
        className="fixed bottom-4 right-4 z-50 w-12 h-12 bg-gradient-to-br from-burgundy to-dark-burgundy rounded-2xl flex items-center justify-center shadow-2xl border-2 border-gold/30 backdrop-blur-sm"
        title={isMuted ? "Unmute music" : "Mute music"}
      >
        {isMuted ? (
          <VolumeX className="w-6 h-6 text-gold" />
        ) : (
          <Volume2 className="w-6 h-6 text-gold" />
        )}
      </motion.button>
    </>
  );
};

export default MusicPlayer;
