// components/PhotoCollection.jsx
import React, { useState, useEffect, useRef } from 'react';

const PhotoCollection = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [uploaded, setUploaded] = useState(false);
  const fileInputRef = useRef(null);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const handleUploadClick = () => {
    setShowForm(true);
    setTimeout(() => {
      document.getElementById('upload-form')?.scrollIntoView({ 
        behavior: 'smooth' 
      });
    }, 100);
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setSelectedFiles([]);
    setUploaded(false);
  };

  const handleFileSelect = (event) => {
    const files = Array.from(event.target.files).slice(0, 5);
    setSelectedFiles(files);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setUploading(true);

    // Simulate upload process (replace with actual upload logic)
    await new Promise(resolve => setTimeout(resolve, 2000));

    setUploading(false);
    setUploaded(true);
    setSelectedFiles([]);
  };

  const removeFile = (index) => {
    setSelectedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const triggerFileInput = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className={`
      max-w-4xl mx-auto p-8 font-cormorant
      transition-all duration-800 ease-out
      ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-8'}
    `}>
      {/* Guest Upload Section */}
      <section className="text-center mb-12 relative">
        {/* Floating Icon */}
        <div className="text-6xl mb-4 animate-float">üì∏</div>

        {/* Header */}
        <h2 className={`
          text-3xl md:text-4xl font-playfair font-bold text-burgundy mb-4
          transition-all duration-700 delay-300
          ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-6'}
        `}>
          Share Your Favorite Moments
        </h2>

        <p className={`
          text-lg text-gray-700 mb-8
          transition-all duration-700 delay-500
          ${isVisible ? 'opacity-100' : 'opacity-0'}
        `}>
          Help us capture beautiful memories by uploading your photos from the wedding
        </p>

        {/* Instructions Grid */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-12">
          {[
            { step: '1', text: 'Click upload button' },
            { step: '2', text: 'Select your photos' },
            { step: '3', text: 'Add your name' },
            { step: '4', text: 'Submit instantly' }
          ].map((item, index) => (
            <div
              key={item.step}
              className="
                bg-gradient-to-br from-burgundy to-dark-burgundy 
                text-cream p-6 rounded-2xl shadow-xl
                transform transition-all duration-500 hover:scale-105 hover:-translate-y-2
                hover:shadow-2xl cursor-pointer
                animate-fade-in-up
              "
              style={{ animationDelay: `${index * 0.2}s` }}
            >
              <div className="flex flex-col items-center">
                <div className="
                  w-12 h-12 bg-cream bg-opacity-20 
                  rounded-full flex items-center justify-center
                  border-2 border-cream border-opacity-40
                  backdrop-blur-sm text-lg font-bold mb-3 text-light-gold
                ">
                  {item.step}
                </div>
                <p className="font-semibold text-center font-cormorant">{item.text}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Upload Button */}
        <div className="text-center">
          <button
            onClick={handleUploadClick}
            className="
              inline-flex items-center gap-3
              bg-gradient-to-r from-gold to-light-gold
              hover:from-gold hover:to-yellow-200
              text-burgundy font-playfair font-bold text-xl
              px-8 py-4 rounded-full
              transform transition-all duration-300
              hover:scale-105 hover:-translate-y-1
              shadow-lg hover:shadow-xl
              border-2 border-gold
              relative overflow-hidden group
              cursor-pointer
            "
          >
            <span className="text-lg group-hover:scale-110 transition-transform">‚ú®</span>
            <span className="text-2xl">üì§</span>
            Upload Your Photos
            <span className="text-lg group-hover:scale-110 transition-transform">‚ú®</span>

            <div className="absolute inset-0 -inset-1 bg-gradient-to-r from-transparent via-cream to-transparent transform -skew-x-12 translate-x-[-100%] group-hover:translate-x-[100%] transition-transform duration-1000" />
          </button>

          <div className={`
            mt-6 p-4
            bg-cream border-2 border-gold
            rounded-xl text-burgundy
            transition-all duration-700 delay-700
            ${isVisible ? 'opacity-100' : 'opacity-0'}
          `}>
            <p className="font-medium font-cormorant">
              <strong>Note:</strong> Upload up to 5 photos. Each photo should be less than 10MB.
            </p>
          </div>
        </div>

        {/* Custom Upload Form */}
        {showForm && (
          <div 
            id="upload-form"
            className="mt-12 animate-fade-in-up"
          >
            <div className="bg-cream border-2 border-gold rounded-2xl p-6 shadow-xl">
              <div className="flex justify-between items-center mb-6">
                <h3 className="text-2xl font-playfair font-bold text-burgundy">
                  Upload Your Photos
                </h3>
                <button
                  onClick={handleCloseForm}
                  className="text-burgundy hover:text-dark-burgundy text-2xl font-bold transition-colors w-8 h-8 flex items-center justify-center"
                >
                  √ó
                </button>
              </div>

              {uploaded ? (
                <div className="text-center py-8">
                  <div className="text-6xl mb-4">üéâ</div>
                  <h4 className="text-2xl font-playfair text-burgundy mb-2">
                    Thank You!
                  </h4>
                  <p className="text-gray-700 mb-4">
                    Your photos have been successfully uploaded.
                  </p>
                  <button
                    onClick={handleCloseForm}
                    className="bg-gold text-burgundy px-6 py-2 rounded-full font-semibold hover:bg-light-gold transition-colors"
                  >
                    Close
                  </button>
                </div>
              ) : (
                <form onSubmit={handleSubmit} className="space-y-6">
                  {/* Name Field */}
                  <div>
                    <label className="block text-burgundy font-semibold mb-2">
                      Your Name (Optional)
                    </label>
                    <input
                      type="text"
                      className="w-full p-3 border-2 border-gold rounded-lg focus:outline-none focus:border-burgundy"
                      placeholder="Enter your name"
                    />
                  </div>

                  {/* File Upload Area */}
                  <div>
                    <label className="block text-burgundy font-semibold mb-2">
                      Select Photos (Max 5)
                    </label>
                    <div
                      onClick={triggerFileInput}
                      className="border-2 border-dashed border-gold rounded-lg p-8 text-center cursor-pointer hover:bg-yellow-50 transition-colors"
                    >
                      <div className="text-4xl mb-2">üìÅ</div>
                      <p className="text-burgundy font-semibold">
                        Click to select photos
                      </p>
                      <p className="text-gray-600 text-sm mt-1">
                        or drag and drop files here
                      </p>
                      <p className="text-gray-500 text-xs mt-2">
                        PNG, JPG, JPEG up to 10MB each
                      </p>
                    </div>
                    <input
                      ref={fileInputRef}
                      type="file"
                      multiple
                      accept="image/*"
                      onChange={handleFileSelect}
                      className="hidden"
                    />
                  </div>

                  {/* Selected Files Preview */}
                  {selectedFiles.length > 0 && (
                    <div className="space-y-3">
                      <h4 className="text-burgundy font-semibold">
                        Selected Files ({selectedFiles.length}/5):
                      </h4>
                      {selectedFiles.map((file, index) => (
                        <div
                          key={index}
                          className="flex items-center justify-between bg-white p-3 rounded-lg border border-gold"
                        >
                          <div className="flex items-center space-x-3">
                            <span className="text-xl">üñºÔ∏è</span>
                            <div>
                              <p className="text-burgundy font-medium">
                                {file.name}
                              </p>
                              <p className="text-gray-500 text-sm">
                                {(file.size / (1024 * 1024)).toFixed(2)} MB
                              </p>
                            </div>
                          </div>
                          <button
                            type="button"
                            onClick={() => removeFile(index)}
                            className="text-red-500 hover:text-red-700"
                          >
                            üóëÔ∏è
                          </button>
                        </div>
                      ))}
                    </div>
                  )}

                  {/* Submit Button */}
                  <button
                    type="submit"
                    disabled={selectedFiles.length === 0 || uploading}
                    className="w-full bg-gradient-to-r from-burgundy to-dark-burgundy text-cream py-3 rounded-lg font-semibold text-lg disabled:opacity-50 disabled:cursor-not-allowed hover:from-dark-burgundy hover:to-burgundy transition-all"
                  >
                    {uploading ? (
                      <span className="flex items-center justify-center">
                        <span className="animate-spin mr-2">‚è≥</span>
                        Uploading...
                      </span>
                    ) : (
                      `Upload ${selectedFiles.length} Photo${selectedFiles.length !== 1 ? 's' : ''}`
                    )}
                  </button>
                </form>
              )}
            </div>
          </div>
        )}

        {/* Floating Decorative Elements */}
        <div className="relative h-24 mt-12">
          {[
            { emoji: 'üñºÔ∏è', position: 'left-10', delay: '0s' },
            { emoji: 'üì∑', position: 'left-1/2 -translate-x-1/2', delay: '2s' },
            { emoji: 'üåü', position: 'right-10', delay: '4s' }
          ].map((item, index) => (
            <div
              key={index}
              className={`
                absolute text-3xl opacity-60 text-gold
                animate-float
                ${item.position}
              `}
              style={{ animationDelay: item.delay }}
            >
              {item.emoji}
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default PhotoCollection;
