// src/components/MusicPlayer.js
import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Volume2, VolumeX, Play, Pause } from 'lucide-react';

const MusicPlayer = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [showPlayer, setShowPlayer] = useState(true);
  const audioRef = useRef(null);

  // Auto-play on component mount
  useEffect(() => {
    const playAudio = async () => {
      try {
        // Start with muted audio for autoplay policies
        if (audioRef.current) {
          audioRef.current.muted = true;
          await audioRef.current.play();
          setIsPlaying(true);

          // Unmute after user interaction
          const unmuteOnInteraction = () => {
            audioRef.current.muted = false;
            setIsMuted(false);
            document.removeEventListener('click', unmuteOnInteraction);
            document.removeEventListener('touchstart', unmuteOnInteraction);
          };

          document.addEventListener('click', unmuteOnInteraction);
          document.addEventListener('touchstart', unmuteOnInteraction);
        }
      } catch (error) {
        console.log('Autoplay failed, waiting for user interaction:', error);
      }
    };

    playAudio();
  }, []);

  const togglePlay = async () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        await audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const toggleMute = () => {
    if (audioRef.current) {
      audioRef.current.muted = !isMuted;
      setIsMuted(!isMuted);
    }
  };

  return (
    <>
      {/* Hidden audio element */}
      <audio
        ref={audioRef}
        loop
        preload="auto"
      >
        <source src="/music/wedding-music.mp3" type="audio/mpeg" />
        <source src="/music/wedding-music.ogg" type="audio/ogg" />
        Your browser does not support the audio element.
      </audio>

      {/* Music Player UI */}
      <AnimatePresence>
        {showPlayer && (
          <motion.div
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 100 }}
            className="fixed bottom-4 right-4 z-50"
          >
            <motion.div
              whileHover={{ scale: 1.05 }}
              className="bg-gradient-to-br from-burgundy to-dark-burgundy rounded-2xl p-3 shadow-2xl border-2 border-gold/30 backdrop-blur-sm"
            >
              <div className="flex items-center gap-3">
                {/* Play/Pause Button */}
                <motion.button
                  whileTap={{ scale: 0.9 }}
                  onClick={togglePlay}
                  className="w-10 h-10 bg-gold rounded-full flex items-center justify-center hover:bg-light-gold transition-colors"
                >
                  {isPlaying ? (
                    <Pause className="w-5 h-5 text-burgundy" />
                  ) : (
                    <Play className="w-5 h-5 text-burgundy" />
                  )}
                </motion.button>

                {/* Volume Control */}
                <motion.button
                  whileTap={{ scale: 0.9 }}
                  onClick={toggleMute}
                  className="w-8 h-8 bg-gold/20 rounded-full flex items-center justify-center hover:bg-gold/30 transition-colors"
                >
                  {isMuted ? (
                    <VolumeX className="w-4 h-4 text-gold" />
                  ) : (
                    <Volume2 className="w-4 h-4 text-gold" />
                  )}
                </motion.button>

                {/* Close Button */}
                <motion.button
                  whileTap={{ scale: 0.9 }}
                  onClick={() => setShowPlayer(false)}
                  className="w-6 h-6 bg-gold/10 rounded-full flex items-center justify-center hover:bg-gold/20 transition-colors"
                >
                  <span className="text-gold text-xs font-bold">Ã—</span>
                </motion.button>
              </div>

              {/* Song Title */}
              <motion.p
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                className="luxury-text text-gold text-xs mt-2 text-center"
              >
                {isPlaying ? "Now Playing" : "Paused"}
              </motion.p>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Show music button when player is hidden */}
      <AnimatePresence>
        {!showPlayer && (
          <motion.button
            initial={{ opacity: 0, scale: 0 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0 }}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            onClick={() => setShowPlayer(true)}
            className="fixed bottom-4 right-4 z-50 w-12 h-12 bg-gradient-to-br from-burgundy to-dark-burgundy rounded-2xl flex items-center justify-center shadow-2xl border-2 border-gold/30 backdrop-blur-sm"
          >
            <Volume2 className="w-6 h-6 text-gold" />
          </motion.button>
        )}
      </AnimatePresence>
    </>
  );
};

export default MusicPlayer;
