// src/components/Location.js
import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { QRCodeSVG } from 'qrcode.react'; // Changed import

const Location = () => {
  const [showQR, setShowQR] = useState(false);
  const venueAddress = "The Grand Palace Hotel, Marine Drive, Mumbai, Maharashtra 400020";
  const googleMapsUrl = `https://maps.google.com/?q=${encodeURIComponent(venueAddress)}`;

  const cardVariants = {
    hidden: { scale: 0.9, opacity: 0 },
    visible: {
      scale: 1,
      opacity: 1,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    }
  };

  return (
    <section className="section-padding bg-gradient-to-br from-burgundy to-dark-burgundy text-white">
      <motion.div
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true }}
        variants={cardVariants}
        className="max-w-4xl mx-auto"
      >
        <h2 className="luxury-heading text-gold text-center mb-12">
          Venue Location
        </h2>

        <div className="gold-accent bg-white p-10 rounded-2xl text-dark-burgundy">
          <h3 className="luxury-heading text-3xl mb-6 text-center">
            The Grand Palace Hotel
          </h3>

          <p className="luxury-text text-xl mb-8 text-center">
            Marine Drive, Mumbai<br/>
            Maharashtra 400020<br/>
            India
          </p>

          <div className="flex justify-center gap-6 flex-wrap mb-6">
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="luxury-btn"
              onClick={() => window.open(googleMapsUrl, '_blank')}
            >
              Open in Google Maps
            </motion.button>

            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="luxury-btn"
              onClick={() => setShowQR(!showQR)}
            >
              {showQR ? 'Hide QR Code' : 'Show QR Code'}
            </motion.button>
          </div>

          {showQR && (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              className="flex justify-center mt-6"
            >
              <div className="p-5 bg-white rounded-xl border-4 border-gold">
                <QRCodeSVG 
                  value={googleMapsUrl} 
                  size={200}
                  level="H"
                  includeMargin
                />
              </div>
            </motion.div>
          )}
        </div>
      </motion.div>
    </section>
  );
};

export default Location;
