// src/components/Footer.js
import React from 'react';
import { motion } from 'framer-motion';
import { Heart, Mail, MapPin, Phone, Instagram, Linkedin, ExternalLink } from 'lucide-react';

const Footer = () => {
  const currentYear = new Date().getFullYear();

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 30, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  const socialLinks = [
    {
      icon: Instagram,
      href: "https://instagram.com/spotcard.in",
      label: "Instagram",
      username: "@spotcard.in"
    },
    {
      icon: Linkedin,
      href: "https://www.linkedin.com/company/spotcard-business-solution",
      label: "LinkedIn",
      username: "SpotCard Business Solution"
    }
  ];

  const contactInfo = [
    {
      icon: Mail,
      text: "hello@spotcard.in",
      href: "mailto:hello@spotcard.in"
    },
    {
      icon: MapPin,
      text: "Business Solutions Provider",
      href: "#"
    },
    {
      icon: Phone,
      text: "+91 XXXXX XXXXX",
      href: "tel:+91XXXXXXXXX"
    }
  ];

  return (
    <footer className="bg-gradient-to-br from-dark-burgundy to-burgundy border-t border-gold/20 relative overflow-hidden">
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-5">
        <div className="absolute inset-0 bg-[radial-gradient(circle_at_1px_1px,white_1px,transparent_0)] bg-[length:30px_30px]"></div>
      </div>

      {/* Floating decorative elements */}
      <div className="absolute inset-0 overflow-hidden">
        <motion.div
          animate={{
            y: [0, -20, 0],
            rotate: [0, 5, 0]
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "easeInOut"
          }}
          className="absolute top-10 left-10 text-gold/10"
        >
          <Heart className="w-12 h-12" />
        </motion.div>
        <motion.div
          animate={{
            y: [0, 15, 0],
            rotate: [0, -5, 0]
          }}
          transition={{
            duration: 6,
            repeat: Infinity,
            ease: "easeInOut",
            delay: 2
          }}
          className="absolute bottom-10 right-10 text-gold/10"
        >
          <ExternalLink className="w-16 h-16" />
        </motion.div>
      </div>

      <motion.div
        variants={containerVariants}
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true, margin: "-50px" }}
        className="max-w-6xl mx-auto px-6 py-12 relative z-10"
      >
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mb-8">
          {/* Company Info */}
          <motion.div variants={itemVariants} className="lg:col-span-1">
            <motion.div
              initial={{ scale: 0 }}
              whileInView={{ scale: 1 }}
              transition={{ 
                type: "spring", 
                stiffness: 200, 
                damping: 15 
              }}
              className="flex items-center gap-3 mb-6"
            >
              <div className="w-12 h-12 bg-gradient-to-r from-gold to-light-gold rounded-2xl flex items-center justify-center shadow-lg">
                <Heart className="w-6 h-6 text-burgundy" />
              </div>
              <div>
                <h3 className="luxury-heading text-2xl text-gold mb-1">
                  SpotCard
                </h3>
                <p className="luxury-text text-white/70 text-sm">
                  Business Solutions
                </p>
              </div>
            </motion.div>

            <p className="luxury-text text-white/80 leading-relaxed mb-6">
              Transforming business interactions with innovative digital solutions and creative excellence.
            </p>

            <motion.div
              initial={{ width: 0 }}
              whileInView={{ width: 80 }}
              transition={{ duration: 1, delay: 0.5 }}
              className="w-20 h-0.5 bg-gradient-to-r from-gold to-light-gold rounded-full mb-6"
            />
          </motion.div>

          {/* Contact Information */}
          <motion.div variants={itemVariants} className="lg:col-span-1">
            <h4 className="luxury-subheading text-xl text-gold mb-6">Get In Touch</h4>
            <div className="space-y-4">
              {contactInfo.map((contact, index) => (
                <motion.a
                  key={index}
                  href={contact.href}
                  variants={itemVariants}
                  whileHover={{ x: 5, scale: 1.02 }}
                  className="flex items-center gap-4 group cursor-pointer"
                >
                  <div className="w-10 h-10 bg-gold/10 rounded-xl flex items-center justify-center group-hover:bg-gold/20 transition-colors">
                    <contact.icon className="w-5 h-5 text-gold" />
                  </div>
                  <span className="luxury-text text-white/80 group-hover:text-gold transition-colors">
                    {contact.text}
                  </span>
                </motion.a>
              ))}
            </div>
          </motion.div>

          {/* Social Links */}
          <motion.div variants={itemVariants} className="lg:col-span-1">
            <h4 className="luxury-subheading text-xl text-gold mb-6">Connect With Us</h4>
            <div className="space-y-4">
              {socialLinks.map((social, index) => (
                <motion.a
                  key={index}
                  href={social.href}
                  target="_blank"
                  rel="noopener noreferrer"
                  variants={itemVariants}
                  whileHover={{ x: 5, scale: 1.02 }}
                  className="flex items-center gap-4 group cursor-pointer"
                >
                  <div className="w-10 h-10 bg-gold/10 rounded-xl flex items-center justify-center group-hover:bg-gold/20 transition-colors">
                    <social.icon className="w-5 h-5 text-gold" />
                  </div>
                  <div>
                    <p className="luxury-text text-white/80 group-hover:text-gold transition-colors">
                      {social.label}
                    </p>
                    <p className="luxury-text text-white/60 text-sm">
                      {social.username}
                    </p>
                  </div>
                </motion.a>
              ))}
            </div>
          </motion.div>
        </div>

        {/* Bottom Bar */}
        <motion.div
          variants={itemVariants}
          className="border-t border-gold/20 pt-8 flex flex-col md:flex-row justify-between items-center gap-4"
        >
          <div className="flex items-center gap-2">
            <p className="luxury-text text-white/70 text-sm">
              © {currentYear} SpotCard Business Solution. All rights reserved.
            </p>
            <motion.div
              animate={{ scale: [1, 1.2, 1] }}
              transition={{ duration: 2, repeat: Infinity }}
              className="text-gold"
            >
              <Heart className="w-4 h-4 fill-current" />
            </motion.div>
          </div>

          <div className="flex items-center gap-6">
            <motion.a
              href="#"
              whileHover={{ scale: 1.05 }}
              className="luxury-text text-white/60 hover:text-gold text-sm transition-colors"
            >
              Privacy Policy
            </motion.a>
            <motion.a
              href="#"
              whileHover={{ scale: 1.05 }}
              className="luxury-text text-white/60 hover:text-gold text-sm transition-colors"
            >
              Terms of Service
            </motion.a>
          </div>
        </motion.div>

        {/* Made with love */}
        <motion.div
          variants={itemVariants}
          className="text-center mt-8"
        >
          <p className="luxury-text text-white/50 text-sm flex items-center justify-center gap-2">
            Crafted with 
            <motion.span
              animate={{ scale: [1, 1.3, 1] }}
              transition={{ duration: 1.5, repeat: Infinity }}
              className="text-gold"
            >
              ♥
            </motion.span>
            by SpotCard Team
          </p>
        </motion.div>
      </motion.div>

      {/* Top decorative border */}
      <motion.div
        initial={{ scaleX: 0 }}
        whileInView={{ scaleX: 1 }}
        transition={{ duration: 1.5, ease: "easeOut" }}
        className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-transparent via-gold to-transparent"
      />
    </footer>
  );
};

export default Footer;
