// src/components/RSVP.js
import React, { useState } from 'react';
import { motion } from 'framer-motion';

const RSVP = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    guests: '1',
    message: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Thank you for your RSVP! We look forward to celebrating with you.');
    setFormData({
      name: '',
      email: '',
      phone: '',
      guests: '1',
      message: ''
    });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <section className="section-padding bg-gradient-to-br from-cream to-light-gold relative overflow-hidden">
      {/* Animated decorative elements */}
      <motion.div
        animate={{ y: [0, -20, 0] }}
        transition={{ duration: 4, repeat: Infinity, ease: "easeInOut" }}
        className="absolute top-10 right-10 w-20 h-20 border-2 border-gold rounded-full opacity-20"
      />

      <motion.div
        initial={{ opacity: 0, y: 50 }}
        whileInView={{ opacity: 1, y: 0 }}
        viewport={{ once: true }}
        transition={{ duration: 0.8 }}
        className="max-w-2xl mx-auto relative z-10"
      >
        <h2 className="luxury-heading text-center mb-8">
          RSVP
        </h2>

        <p className="luxury-text text-xl text-center mb-12">
          Please let us know if you can join our special day
        </p>

        <form onSubmit={handleSubmit} className="gold-accent bg-white p-10 rounded-2xl">
          <div className="space-y-6">
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Your Full Name"
              required
              className="w-full p-4 border-2 border-gold rounded-lg text-xl font-cormorant"
            />

            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Your Email"
              required
              className="w-full p-4 border-2 border-gold rounded-lg text-xl font-cormorant"
            />

            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              placeholder="Your Phone Number"
              required
              className="w-full p-4 border-2 border-gold rounded-lg text-xl font-cormorant"
            />

            <select
              name="guests"
              value={formData.guests}
              onChange={handleChange}
              className="w-full p-4 border-2 border-gold rounded-lg text-xl font-cormorant"
            >
              <option value="1">1 Guest</option>
              <option value="2">2 Guests</option>
              <option value="3">3 Guests</option>
              <option value="4">4 Guests</option>
              <option value="5">5+ Guests</option>
            </select>

            <textarea
              name="message"
              value={formData.message}
              onChange={handleChange}
              placeholder="Any dietary restrictions or special requests?"
              rows="4"
              className="w-full p-4 border-2 border-gold rounded-lg text-xl font-cormorant resize-vertical"
            />
          </div>

          <motion.button
            type="submit"
            className="luxury-btn w-full mt-8"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Confirm Attendance
          </motion.button>
        </form>
      </motion.div>
    </section>
  );
};

export default RSVP;
