// src/components/RSVP.js
import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Heart, Users, Calendar } from 'lucide-react';

const RSVP = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    guests: '1',
    attending: 'yes',
    message: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Thank you for your RSVP! We look forward to celebrating with you.');
    setFormData({
      name: '',
      email: '',
      phone: '',
      guests: '1',
      attending: 'yes',
      message: ''
    });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <section className="section-padding bg-gradient-to-br from-burgundy to-dark-burgundy relative overflow-hidden">
      {/* Animated decorative elements */}
      <motion.div
        animate={{ y: [0, -20, 0] }}
        transition={{ duration: 4, repeat: Infinity, ease: "easeInOut" }}
        className="absolute top-10 right-10 w-20 h-20 border-2 border-gold rounded-full opacity-20"
      />

      <motion.div
        animate={{ y: [0, 20, 0] }}
        transition={{ duration: 5, repeat: Infinity, ease: "easeInOut", delay: 1 }}
        className="absolute bottom-20 left-10 w-16 h-16 border-2 border-gold rounded-full opacity-20"
      />

      <motion.div
        initial={{ opacity: 0, y: 50 }}
        whileInView={{ opacity: 1, y: 0 }}
        viewport={{ once: true }}
        transition={{ duration: 0.8 }}
        className="max-w-2xl mx-auto relative z-10"
      >
        {/* Header with icons */}
        <div className="text-center mb-8">
          <motion.div
            initial={{ scale: 0 }}
            whileInView={{ scale: 1 }}
            transition={{ 
              type: "spring", 
              stiffness: 200, 
              damping: 15,
              delay: 0.2 
            }}
            className="inline-flex items-center gap-4 mb-6"
          >
            <div className="w-12 h-12 bg-gradient-to-r from-gold to-light-gold rounded-2xl flex items-center justify-center shadow-lg">
              <Calendar className="w-6 h-6 text-burgundy" />
            </div>
            <h2 className="luxury-heading text-4xl text-gold">
              RSVP
            </h2>
            <div className="w-12 h-12 bg-gradient-to-r from-gold to-light-gold rounded-2xl flex items-center justify-center shadow-lg">
              <Heart className="w-6 h-6 text-burgundy" />
            </div>
          </motion.div>

          <motion.div
            initial={{ width: 0 }}
            whileInView={{ width: 80 }}
            transition={{ duration: 1, delay: 0.5 }}
            className="w-20 h-1 bg-gradient-to-r from-gold to-light-gold mx-auto rounded-full mb-6"
          />
        </div>

        <motion.p 
          className="luxury-text text-xl text-center text-white/80 mb-12"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.3 }}
        >
          Please let us know if you can join our blessed celebration
        </motion.p>

        <motion.form 
          onSubmit={handleSubmit} 
          className="modern-card bg-white/10 backdrop-blur-sm p-8 rounded-3xl border-2 border-gold/30 shadow-2xl"
          initial={{ opacity: 0, scale: 0.9 }}
          whileInView={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8, delay: 0.4 }}
        >
          <div className="space-y-6">
            {/* Attendance Selection */}
            <div className="flex gap-4 mb-6">
              <label className="flex items-center gap-2 luxury-text text-white cursor-pointer">
                <input
                  type="radio"
                  name="attending"
                  value="yes"
                  checked={formData.attending === 'yes'}
                  onChange={handleChange}
                  className="w-5 h-5 text-gold focus:ring-gold"
                />
                <span className="flex items-center gap-2">
                  <Heart className="w-4 h-4 text-gold fill-current" />
                  Joyfully Accept
                </span>
              </label>
              <label className="flex items-center gap-2 luxury-text text-white cursor-pointer">
                <input
                  type="radio"
                  name="attending"
                  value="no"
                  checked={formData.attending === 'no'}
                  onChange={handleChange}
                  className="w-5 h-5 text-gold focus:ring-gold"
                />
                <span>Regretfully Decline</span>
              </label>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  placeholder="Your Full Name"
                  required
                  className="w-full p-4 border-2 border-gold/50 rounded-2xl text-lg font-cormorant bg-white/5 text-white placeholder-white/60 focus:bg-white/10 focus:border-gold transition-all duration-300"
                />
              </div>

              <div>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="Your Email"
                  required
                  className="w-full p-4 border-2 border-gold/50 rounded-2xl text-lg font-cormorant bg-white/5 text-white placeholder-white/60 focus:bg-white/10 focus:border-gold transition-all duration-300"
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  placeholder="Your Phone Number"
                  required
                  className="w-full p-4 border-2 border-gold/50 rounded-2xl text-lg font-cormorant bg-white/5 text-white placeholder-white/60 focus:bg-white/10 focus:border-gold transition-all duration-300"
                />
              </div>

              <div className="relative">
                <Users className="absolute left-4 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gold" />
                <select
                  name="guests"
                  value={formData.guests}
                  onChange={handleChange}
                  className="w-full p-4 pl-12 border-2 border-gold/50 rounded-2xl text-lg font-cormorant bg-white/5 text-white focus:bg-white/10 focus:border-gold transition-all duration-300 appearance-none"
                >
                  <option value="1" className="text-dark-burgundy">1 Guest</option>
                  <option value="2" className="text-dark-burgundy">2 Guests</option>
                  <option value="3" className="text-dark-burgundy">3 Guests</option>
                  <option value="4" className="text-dark-burgundy">4 Guests</option>
                  <option value="5" className="text-dark-burgundy">5+ Guests</option>
                </select>
              </div>
            </div>

            <div>
              <textarea
                name="message"
                value={formData.message}
                onChange={handleChange}
                placeholder="Any dietary restrictions or special requests? Your prayers and blessings are also welcome..."
                rows="4"
                className="w-full p-4 border-2 border-gold/50 rounded-2xl text-lg font-cormorant bg-white/5 text-white placeholder-white/60 focus:bg-white/10 focus:border-gold transition-all duration-300 resize-vertical"
              />
            </div>
          </div>

          <motion.button
            type="submit"
            className="luxury-btn w-full mt-8 bg-gradient-to-r from-gold to-light-gold text-burgundy font-semibold py-4 rounded-2xl shadow-lg"
            whileHover={{ 
              scale: 1.05,
              boxShadow: "0 10px 30px rgba(180, 150, 100, 0.3)"
            }}
            whileTap={{ scale: 0.95 }}
          >
            Confirm Attendance
          </motion.button>

          <motion.p 
            className="luxury-text text-center text-white/60 mt-6 text-sm"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            Your presence will add blessings to our special day
          </motion.p>
        </motion.form>
      </motion.div>

      {/* Bottom decorative border */}
      <motion.div
        initial={{ scaleX: 0 }}
        whileInView={{ scaleX: 1 }}
        transition={{ duration: 1.5, ease: "easeOut" }}
        className="absolute bottom-0 left-0 right-0 h-2 bg-gradient-to-r from-transparent via-gold to-transparent"
      />
    </section>
  );
};

export default RSVP;
