// src/components/Hero.js
import React from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    }
  };

  const floatVariants = {
    float: {
      y: [0, -20, 0],
      transition: {
        duration: 6,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  return (
    <section className="section-padding bg-gradient-to-br from-cream via-white to-light-gold relative overflow-hidden">
      {/* Modern background elements */}
      <div className="absolute inset-0 bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-gold/5 via-transparent to-transparent"></div>

      {/* Floating elements */}
      <motion.div
        variants={floatVariants}
        animate="float"
        className="absolute top-1/4 left-1/4 w-20 h-20 sm:w-24 sm:h-24 border border-gold/30 rounded-full opacity-20"
      />
      <motion.div
        variants={floatVariants}
        animate="float"
        transition={{ delay: 2 }}
        className="absolute bottom-1/4 right-1/4 w-16 h-16 sm:w-20 sm:h-20 border border-gold/30 rounded-full opacity-20"
      />

      <motion.div
        variants={containerVariants}
        initial="hidden"
        animate="visible"
        className="relative z-10 text-center w-full max-w-6xl mx-auto"
      >
        <motion.div
          variants={itemVariants}
          className="modern-card mb-8 mx-auto max-w-2xl"
        >
          <motion.p 
            variants={itemVariants}
            className="luxury-text text-gold font-semibold text-lg sm:text-xl mb-4"
          >
            We're Getting Married
          </motion.p>

          <motion.h1 
            variants={itemVariants}
            className="luxury-heading mb-6"
          >
            Sarah <span className="text-gold">&</span> Michael
          </motion.h1>

          <motion.div
            variants={itemVariants}
            className="w-32 h-0.5 bg-gradient-to-r from-transparent via-gold to-transparent mx-auto mb-6"
          />

          <motion.p 
            variants={itemVariants}
            className="luxury-text text-xl sm:text-2xl mb-8"
          >
            December 15, 2024
          </motion.p>
        </motion.div>

        <motion.div
          variants={itemVariants}
          className="relative mx-auto w-64 h-64 sm:w-80 sm:h-80 md:w-96 md:h-96 rounded-3xl overflow-hidden gold-accent shadow-2xl"
        >
          {/* Option 1: If image is in public folder */}
          <img 
            src="/img1.png" 
            alt="Sarah & Michael"
            className="w-full h-full object-cover"
            onError={(e) => {
              e.target.style.display = 'none';
              e.target.nextSibling.style.display = 'block';
            }}
          />

          {/* Fallback if image doesn't load */}
          <div 
            className="w-full h-full bg-gradient-to-br from-gold/20 to-burgundy/20 hidden flex items-center justify-center"
            style={{ display: 'none' }}
          >
            <span className="text-4xl text-burgundy">ðŸ’‘</span>
          </div>

          {/* Decorative corners */}
          <div className="absolute top-4 left-4 w-6 h-6 border-t-2 border-l-2 border-gold"></div>
          <div className="absolute top-4 right-4 w-6 h-6 border-t-2 border-r-2 border-gold"></div>
          <div className="absolute bottom-4 left-4 w-6 h-6 border-b-2 border-l-2 border-gold"></div>
          <div className="absolute bottom-4 right-4 w-6 h-6 border-b-2 border-r-2 border-gold"></div>
        </motion.div>

        <motion.div
          variants={itemVariants}
          className="mt-8"
        >
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="luxury-btn"
            onClick={() => document.getElementById('information').scrollIntoView({ behavior: 'smooth' })}
          >
            View Invitation
          </motion.button>
        </motion.div>
      </motion.div>
    </section>
  );
};

export default Hero;
