// src/components/Information.js
import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Calendar, 
  Clock, 
  MapPin, 
  Users, 
  Heart,
  Sparkles,
  ChevronDown,
  ChevronUp
} from 'lucide-react';

const Information = () => {
  const [activeTab, setActiveTab] = useState('ceremony');
  const [expandedCard, setExpandedCard] = useState(null);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.15
      }
    }
  };

  const itemVariants = {
    hidden: { 
      opacity: 0, 
      y: 40,
      scale: 0.95
    },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        duration: 0.6,
        ease: [0.25, 0.1, 0.25, 1]
      }
    }
  };

  const cardVariants = {
    hidden: { 
      opacity: 0,
      y: 30,
      rotateX: -10
    },
    visible: {
      opacity: 1,
      y: 0,
      rotateX: 0,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    },
    hover: {
      y: -8,
      scale: 1.02,
      transition: {
        duration: 0.3,
        ease: "easeInOut"
      }
    }
  };

  const eventDetails = {
    ceremony: {
      icon: 'ðŸ’’',
      title: 'Wedding Ceremony',
      color: 'from-blue-500/20 to-purple-500/20',
      borderColor: 'border-blue-200/50',
      details: [
        {
          icon: Calendar,
          title: 'December 15, 2024',
          subtitle: 'Saturday Evening',
          description: 'Join us as we exchange our sacred vows in a traditional ceremony filled with love and blessings.'
        },
        {
          icon: Clock,
          title: '4:00 PM - 5:30 PM',
          subtitle: 'Sacred Vows',
          description: 'The ceremony will begin promptly at 4:00 PM. Please arrive 30 minutes early for seating.'
        },
        {
          icon: MapPin,
          title: 'Grand Palace Hotel',
          subtitle: 'Marine Drive, Mumbai',
          description: 'A beautiful waterfront venue with stunning views of the Arabian Sea. Valet parking available.'
        },
        {
          icon: Users,
          title: 'Guest Attire',
          subtitle: 'Formal Traditional Wear',
          description: 'Traditional Indian attire or formal western wear. Embrace the elegance of our special day.'
        }
      ]
    },
    reception: {
      icon: 'ðŸŽ‰',
      title: 'Reception Celebration',
      color: 'from-pink-500/20 to-orange-500/20',
      borderColor: 'border-pink-200/50',
      details: [
        {
          icon: Calendar,
          title: 'December 15, 2024',
          subtitle: 'Saturday Night',
          description: 'An evening of celebration, fine dining, and dancing under the stars.'
        },
        {
          icon: Clock,
          title: '6:00 PM onwards',
          subtitle: 'Dinner & Festivities',
          description: 'Cocktail hour begins at 6:00 PM, followed by dinner and dancing throughout the night.'
        },
        {
          icon: MapPin,
          title: 'Grand Ballroom',
          subtitle: 'Same Venue',
          description: 'The celebration continues in our beautifully decorated grand ballroom with live music.'
        },
        {
          icon: Users,
          title: 'Evening Attire',
          subtitle: 'Cocktail & Celebration',
          description: 'Elegant cocktail attire. Come ready to dance and celebrate with us!'
        }
      ]
    }
  };

  const toggleCard = (index) => {
    setExpandedCard(expandedCard === index ? null : index);
  };

  return (
    <section id="information" className="relative bg-gradient-to-b from-white to-cream/30 overflow-hidden">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        <motion.div
          animate={{
            scale: [1, 1.1, 1],
            opacity: [0.3, 0.5, 0.3],
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "easeInOut"
          }}
          className="absolute top-1/4 left-1/4 w-72 h-72 bg-gold/5 rounded-full blur-3xl"
        />
        <motion.div
          animate={{
            scale: [1.1, 1, 1.1],
            opacity: [0.4, 0.2, 0.4],
          }}
          transition={{
            duration: 6,
            repeat: Infinity,
            ease: "easeInOut",
            delay: 1
          }}
          className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-burgundy/5 rounded-full blur-3xl"
        />
      </div>

      {/* Floating Particles */}
      <div className="absolute inset-0">
        {[...Array(15)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-2 h-2 bg-gold/30 rounded-full"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              y: [0, -20, 0],
              opacity: [0, 1, 0],
            }}
            transition={{
              duration: 3 + Math.random() * 2,
              repeat: Infinity,
              delay: Math.random() * 2,
            }}
          />
        ))}
      </div>

      <div className="section-padding relative z-10">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, margin: "-50px" }}
          className="w-full max-w-6xl mx-auto"
        >
          {/* Header Section */}
          <motion.div variants={itemVariants} className="text-center mb-16">
            <motion.div
              initial={{ scale: 0 }}
              whileInView={{ scale: 1 }}
              transition={{ 
                type: "spring", 
                stiffness: 200, 
                damping: 15,
                delay: 0.2 
              }}
              className="inline-flex items-center gap-3 mb-6"
            >
              <Sparkles className="w-8 h-8 text-gold" />
              <h2 className="luxury-heading bg-gradient-to-r from-burgundy to-gold bg-clip-text text-transparent">
                Our Wedding Day
              </h2>
              <Sparkles className="w-8 h-8 text-gold" />
            </motion.div>

            <motion.div
              initial={{ width: 0 }}
              whileInView={{ width: 96 }}
              transition={{ duration: 1, delay: 0.5 }}
              className="w-24 h-1 bg-gradient-to-r from-gold to-light-gold mx-auto rounded-full mb-6"
            />

            <motion.p 
              variants={itemVariants}
              className="luxury-text text-xl text-dark-burgundy/80 max-w-2xl mx-auto leading-relaxed"
            >
              Join us for an unforgettable celebration of love, commitment, and new beginnings
            </motion.p>
          </motion.div>

          {/* Interactive Tabs */}
          <motion.div variants={itemVariants} className="flex justify-center mb-12">
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-2 border border-gold/20 shadow-lg">
              {Object.entries(eventDetails).map(([key, event]) => (
                <button
                  key={key}
                  onClick={() => setActiveTab(key)}
                  className={`px-8 py-4 rounded-xl font-playfair font-semibold text-lg transition-all duration-300 ${
                    activeTab === key
                      ? 'bg-gradient-to-r from-gold to-light-gold text-burgundy shadow-lg'
                      : 'text-dark-burgundy/70 hover:text-burgundy hover:bg-white/50'
                  }`}
                >
                  <span className="flex items-center gap-3">
                    <span className="text-2xl">{event.icon}</span>
                    {event.title}
                  </span>
                </button>
              ))}
            </div>
          </motion.div>

          {/* Event Details */}
          <AnimatePresence mode="wait">
            <motion.div
              key={activeTab}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
              className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-12"
            >
              {eventDetails[activeTab].details.map((detail, index) => (
                <motion.div
                  key={index}
                  variants={cardVariants}
                  initial="hidden"
                  animate="visible"
                  whileHover="hover"
                  className={`bg-white/90 backdrop-blur-sm rounded-2xl p-6 border-2 ${eventDetails[activeTab].borderColor} shadow-xl cursor-pointer overflow-hidden`}
                  onClick={() => toggleCard(index)}
                >
                  <div className="flex items-start gap-4">
                    <motion.div 
                      whileHover={{ scale: 1.1, rotate: 5 }}
                      className="flex-shrink-0 w-14 h-14 bg-gradient-to-br from-gold to-light-gold rounded-2xl flex items-center justify-center shadow-lg"
                    >
                      <detail.icon className="w-6 h-6 text-burgundy" />
                    </motion.div>

                    <div className="flex-1 min-w-0">
                      <h3 className="luxury-subheading text-lg mb-1 text-burgundy">
                        {detail.title}
                      </h3>
                      <p className="luxury-text text-gold font-semibold text-sm mb-2">
                        {detail.subtitle}
                      </p>

                      <AnimatePresence>
                        {expandedCard === index && (
                          <motion.p
                            initial={{ opacity: 0, height: 0 }}
                            animate={{ opacity: 1, height: 'auto' }}
                            exit={{ opacity: 0, height: 0 }}
                            className="luxury-text text-dark-burgundy/80 text-sm leading-relaxed"
                          >
                            {detail.description}
                          </motion.p>
                        )}
                      </AnimatePresence>
                    </div>

                    <motion.div
                      animate={{ rotate: expandedCard === index ? 180 : 0 }}
                      className="flex-shrink-0 w-8 h-8 bg-gold/10 rounded-full flex items-center justify-center"
                    >
                      {expandedCard === index ? (
                        <ChevronUp className="w-4 h-4 text-gold" />
                      ) : (
                        <ChevronDown className="w-4 h-4 text-gold" />
                      )}
                    </motion.div>
                  </div>
                </motion.div>
              ))}
            </motion.div>
          </AnimatePresence>

          {/* Special Note Card */}
          <motion.div
            variants={itemVariants}
            whileHover={{ 
              scale: 1.02,
              transition: { duration: 0.3 }
            }}
            className="relative overflow-hidden rounded-3xl bg-gradient-to-br from-white to-gold/10 border-2 border-gold/30 shadow-2xl"
          >
            {/* Animated Background */}
            <motion.div
              animate={{
                backgroundPosition: ['0% 0%', '100% 100%'],
              }}
              transition={{
                duration: 10,
                repeat: Infinity,
                ease: "linear"
              }}
              className="absolute inset-0 bg-gradient-to-br from-transparent via-gold/5 to-transparent bg-[length:200%_200%]"
            />

            <div className="relative z-10 p-8 text-center">
              <motion.div
                animate={{
                  rotate: [0, 5, 0, -5, 0],
                }}
                transition={{
                  duration: 4,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
                className="inline-flex items-center gap-3 mb-6"
              >
                <Heart className="w-8 h-8 text-burgundy fill-current" />
                <h3 className="luxury-subheading text-2xl text-burgundy">
                  A Heartfelt Note
                </h3>
                <Heart className="w-8 h-8 text-burgundy fill-current" />
              </motion.div>

              <motion.p
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.2 }}
                className="luxury-text text-lg leading-relaxed text-dark-burgundy/90 max-w-3xl mx-auto mb-6"
              >
                Your presence is the greatest gift we could ask for as we begin our forever together. 
                Come ready to celebrate with love, laughter, and joy that will create memories to last a lifetime.
              </motion.p>

              <motion.div
                initial={{ scale: 0 }}
                whileInView={{ scale: 1 }}
                transition={{ 
                  type: "spring", 
                  stiffness: 200, 
                  damping: 15,
                  delay: 0.5 
                }}
                className="w-32 h-1 bg-gradient-to-r from-gold to-light-gold mx-auto rounded-full"
              />
            </div>
          </motion.div>

          {/* Countdown Timer (Optional Addition) */}
          <motion.div
            variants={itemVariants}
            className="text-center mt-12"
          >
            <motion.div
              whileHover={{ scale: 1.05 }}
              className="inline-flex items-center gap-4 px-6 py-3 bg-white/80 backdrop-blur-sm rounded-2xl border border-gold/20 shadow-lg cursor-pointer"
            >
              <Calendar className="w-6 h-6 text-gold" />
              <span className="luxury-text text-burgundy font-semibold">
                Save the Date: December 15, 2024
              </span>
            </motion.div>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Information;
